mapper.insert:
INSERT INTO user (id
    , password
    , user_name
    , authority
    , phone_number
    , email
    , corp_id)
VALUES (#{id}
    , #{password}
    , #{user_name}
    , #{authority}
    , #{phoneNumber}
    , #{email}
    , #{corpId})

mapper.update:
    	UPDATE user
		SET email=#{email}, phone_number=#{phoneNumber}
		WHERE id=#{id} AND password=#{password};

    	UPDATE user
		SET password=#{new_password}
		WHERE id=#{id} AND password=#{password}

mapper.delete:
DELETE
FROM user
WHERE id=#{id} AND password=#{password}

mapper.update:
UPDATE user
SET password=#{password}
WHERE id=#{id}

mapper.select:
SELECT MAX(email)
FROM user
WHERE id=#{id} AND email=#{email}

SELECT password
FROM user
WHERE id=#{id}
