SELECT * 
FROM software
WHERE corp_id = #{corp_id}
AND id NOT IN(SELECT sw_id FROM license WHERE user_id = #{user_id})
AND id NOT IN(SELECT sw_id FROM req_license WHERE state != 3 AND user_id=#{user_id})
ORDER BY sw_name ASC;

